.segment	"CODE"

.proc	_bg_collision_sub2: near

.segment	"CODE"

;
; if(temp_y >= 0xf0) return 0;
;
	lda     _temp_y
	cmp     #$F0
	ldx     #$00
	bcc     L000D
	txa
	rts
;
; coordinates = (temp_x >> 4) + (temp_y & 0xf0);
;
L000D:	lda     _temp_x
	lsr     a
	lsr     a
	lsr     a
	lsr     a
	sta     ptr1
	lda     _temp_y
	and     #$F0
	clc
	adc     ptr1
	sta     _coordinates
;
; collision = c_map[coordinates];
;
	ldy     _coordinates
	lda     _c_map,y
	sta     _collision
;
; if(collision == 0 || collision == 1 || collision == 2 || collision == 3) {
;
	lda     _collision
	beq     L0014
	cmp     #$01
	beq     L0010
	cmp     #$02
	beq     L0010
	cmp     #$03
	bne     L0011
;
; return 0;
;
L0010:	txa
L0014:	rts
;
; if(collision == 14 || collision == 15) {
;
L0011:	lda     _collision
	cmp     #$0E
	beq     L0012
	cmp     #$0F
	bne     L0013
;
; BoxGuy2.x = 0x1000;
;
L0012:	ldx     #$10
	lda     #$00
	sta     _BoxGuy2
	stx     _BoxGuy2+1
;
; BoxGuy2.y = 0xd000;
;
	ldx     #$D0
	sta     _BoxGuy2+2
	stx     _BoxGuy2+2+1
;
; if(collision == 18) {
;
L0013:	lda     _collision
	cmp     #$12
	bne     L000A
;
; BLUE_WIN = 1;
;
	lda     #$01
	sta     _BLUE_WIN
;
; return is_solid[collision];;
;
L000A:	ldy     _collision
	ldx     #$00
	lda     _is_solid,y
;
; }
;
	rts

.endproc

